import React, {useContext, useEffect, useState} from 'react';
import {View, StyleSheet, Pressable, Image} from 'react-native';
import {Button, Text, HelperText} from 'react-native-paper';
import {useGetUser} from "../api/user";
import {AuthContext} from "../assets/contexts/AuthContext";
import Icon from "@expo/vector-icons/Entypo";
import {useSafeAreaInsets} from "react-native-safe-area-context";
import {homeStyles as styles} from "../assets/styles/styles";
import {Header} from "../components/Header";
import {useGetMalls} from "../api/malls";
import {LoadingScreen} from "../components/LoadingScreen";

const Home = ({navigation}: any) => {
    const {data: user} = useGetUser();
    const {data: malls, status, error} = useGetMalls();
    const insets = useSafeAreaInsets();

    const {token, logout} = useContext(AuthContext);

    if (!user) {
        return (
            <LoadingScreen/>
        )
    }

    return (
        <View style={{
            flex: 1,
            paddingTop: insets.top,
            paddingBottom: insets.bottom,
            paddingLeft: insets.left,
            paddingRight: insets.right,
        }}>
            <View style={styles.container}>
                <Image source={require('../assets/images/logo.png')} style={styles.logo}/>
                <Text style={styles.heading}>Welcome {user?.username}</Text>
                <View style={styles.grid}>
                    <View style={styles.gridRow}>
                        <Pressable
                            style={styles.iconButton}
                            onPress={() => navigation.navigate('Entry')}
                        >
                            <Icon name="login" size={50} color="white"/>
                            <Text style={styles.iconButtonLabel}>Entry</Text>
                        </Pressable>
                        <Pressable
                            style={styles.iconButton}
                            onPress={() => navigation.navigate('Entry')}
                        >
                            <Icon name="login" size={50} color="white"/>
                            <Text style={styles.iconButtonLabel}>Entry</Text>
                        </Pressable>
                    </View>
                    <View style={styles.gridRow}>
                        <Pressable
                            style={styles.iconButton}
                            onPress={() => navigation.navigate('Entry')}
                        >
                            <Icon name="login" size={50} color="white"/>
                            <Text style={styles.iconButtonLabel}>Entry</Text>
                        </Pressable>
                        <Pressable
                            style={styles.iconButton}
                            onPress={() => navigation.navigate('Entry')}
                        >
                            <Icon name="login" size={50} color="white"/>
                            <Text style={styles.iconButtonLabel}>Entry</Text>
                        </Pressable>
                    </View>
                </View>
                <Button
                    mode='contained'
                    style={styles.button}
                    onPress={async () => {
                        logout().then(() => console.log("Logged out"));
                    }}
                >
                    Logout
                </Button>
            </View>
        </View>
    )
}

export default Home;
