import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'space-evenly',
        backgroundColor: '#F5F5F5',
        padding: 16,
        width: '100%',
    },
    loading: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    heading: {
        fontSize: 24,
        fontWeight: 'bold',
        marginVertical: 16,
        lineHeight: 32,
        textAlign: 'center',
    },
    subheading: {
        fontSize: 16,
        marginBottom: 32,
    },
    logo: {
        resizeMode: 'contain',
        width: 150,
        height: 150,
    },
    button: {
        marginVertical: 16,
    },
});

export const homeStyles = StyleSheet.create({
    ...styles,
    iconButton: {
        flexDirection: 'column',
        backgroundColor: '#000000',
        borderRadius: 10,
        margin: 5,
        paddingHorizontal: 30,
        paddingVertical: 20,
        alignItems: 'center',
        justifyContent: 'center',
    },
    iconButtonLabel: {
        fontSize: 20,
        fontWeight: 'bold',
        color: 'white',
    },
    grid: {
        flexDirection: 'column',
        flexWrap: 'wrap',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
    },
    gridRow: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
    }
});

export const loginStyles = StyleSheet.create({
    ...styles,
    input: {
        width: '80%',
        marginVertical: 8,
    },
    helperText: {
        marginLeft: '10%',
    },
});

export const registerStyles = StyleSheet.create({
    ...styles,
    ...loginStyles,
    form: {
        flex: 1,
        width: '100%',
        justifyContent: 'center',
        alignItems: 'center',
    },
    buttonContainer: {
        width: '100%',
        position: 'absolute',
        bottom: 0,
        padding: 20,
        alignItems: 'center',
        backgroundColor: '#F5F5F5',
    }
});

export const entryStyles = StyleSheet.create({
    ...styles,
    qrContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#F5F5F5',
        borderRadius: 10,
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 0,
        },
        shadowOpacity: 0.25,
        shadowRadius: 10,
        elevation: 5,
        height: 250,
        width: 250,
        margin: 10,
    },
    refreshButton: {
        margin: 20,
    },
    qrCode: {
        width: 200,
        height: 200,
        margin: 0,
        padding: 0,
    },
    row: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'center',
        width: '100%',
    },
    field: {
        fontSize: 18,
    },
    data: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    actionButton: {
        alignItems: 'center',
        justifyContent: 'center',
        padding: 8,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'black',
    },
    actionButtonLabel: {
        fontSize: 14,
        fontWeight: 'bold',
        color: 'white',
    },
});

export const headerStyles = StyleSheet.create({
    ...styles,
    header: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'black',
        padding: 16,
        color: 'white',
    },
    headerText: {
        fontSize: 24,
        fontWeight: 'bold',
        color: 'white',
    },
    backButton: {
        flexDirection: 'row',
        alignItems: 'center',
        margin: 10,
        position: 'absolute',
        left: 0,
        color: 'white',
    },
    backButtonLabel: {
        fontSize: 12,
        fontWeight: 'bold',
        color: 'white',
    },
});
